para.example:
   Operacje bitowe AND, OR i XOR na liczbach


<?php
/*
 * Zignoruj tę sekcję,
 * służy ona tylko formatowaniu, aby wynik był bardziej czytelny.
 */

$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
        . ' %3$s (%4$2d = %4$04b)' . "\n";

echo <<<EOH
 ---------     ---------  -- ---------
 wynik         wartość    op test
 ---------     ---------  -- ---------
EOH;


/*
 * Przykłady są tutaj.
 */

$values = array(0, 1, 2, 4, 8);
$test = 1 + 4;

echo "\n Bitowe AND \n";
foreach ($values as $value) {
    $result = $value & $test;
    printf($format, $result, $value, '&', $test);
}

echo "\n Bitowe OR \n";
foreach ($values as $value) {
    $result = $value | $test;
    printf($format, $result, $value, '|', $test);
}

echo "\n Bitowe wyłączne OR (XOR) \n";
foreach ($values as $value) {
    $result = $value ^ $test;
    printf($format, $result, $value, '^', $test);
}
?>


   &example.outputs;


 ---------     ---------  -- ---------
 wynik         wartość    op test
 ---------     ---------  -- ---------
 Bitowe AND
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 Bitowe OR
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 Bitowe wyłączne OR (XOR)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

Przesunięcia bitowe na liczbach
<?php /* * Przykłady. */
echo "\n--- PRZESUNIĘCIA W PRAWO NA LICZBACH DODATNICH ---\n";
$val = 4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'kopia bitu znaku przesunięta w lewą stronę');
$val = 4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places);
$val = 4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'przesunięcie bitów na prawą stronę');
$val = 4; $places = 4; $res = $val >> $places; p($res, $val, '>>', $places, 'taki sam wynik jak powyżej; nie można przesunąć poza 0');
echo "\n--- PRZESUNIĘCIA W PRAWO NA LICZBACH UJEMNYCH ---\n";
$val = -4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'kopia bitu znaku przesunięta w lewą stronę');
$val = -4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places, 'przesunięcie bitów na prawą stronę');
$val = -4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'taki sam wynik jak powyżej; nie można przesunąć poza -1');
echo "\n--- PRZESUNIĘCIA W LEWO NA LICZBACH DODATNICH ---\n";
$val = 4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'dopełnienie zerami z prawej strony');
$val = 4; $places = (PHP_INT_SIZE * 8) - 4; $res = $val << $places; p($res, $val, '<<', $places);
$val = 4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places, 'sign bits get shifted out');
$val = 4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits shift out left side');
echo "\n--- PRZESUNIĘCIA W LEWO NA LICZBACH UJEMNYCH ---\n";
$val = -4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'dopełnienie zerami z prawej strony');
$val = -4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places);
$val = -4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits shift out left side, including sign bit');
/* * Możesz zignorować tę sekcję, * służy ona tylko formatowaniu wyniku, aby był czytelniejszy. */
function p($res, $val, $op, $places, $note = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("Wyrażenie: %d = %d %s %d\n", $res, $val, $op, $places);
echo " Dziesiętna:\n"; printf(" val=%d\n", $val); printf(" res=%d\n", $res);
echo " Binarnie:\n"; printf(' val=' . $format, $val); printf(' res=' . $format, $res);
if ($note) { echo " UWAGI: $note\n"; }
echo "\n"; } ?>
