refentry.refsect1:
&reftitle.returnvalues;


 &return.success;




  Zwracana wartość zostanie skonwertowana do typu boolean, jeżeli została zwrócona inna.

  &reftitle.examples;



    Przykład użycia ArrayAccess::offsetExists


<?php
class obj implements arrayaccess {
    public function offsetSet($offset, $value) {
        var_dump(__METHOD__);
    }
    public function offsetExists($var) {
        var_dump(__METHOD__);
        if ($var == "foobar") {
            return true;
        }
        return false;
    }
    public function offsetUnset($var) {
        var_dump(__METHOD__);
    }
    public function offsetGet($var) {
        var_dump(__METHOD__);
        return "wartość";
    }
}

$obj = new obj;

echo "Wykonuje obj::offsetExists()\n";
var_dump(isset($obj["foobar"]));

echo "\nWykonuje obj::offsetExists() i obj::offsetGet()\n";
var_dump(empty($obj["foobar"]));

echo "\nWykonuje obj::offsetExists(), *nie* obj:offsetGet(), gdyż nie ma nic do pobrania\n";
var_dump(empty($obj["foobaz"]));
?>


    &example.outputs.similar;


Wykonuje obj::offsetExists()
string(17) "obj::offsetExists"
bool(true)

Wykonuje obj::offsetExists() and obj::offsetGet()
string(17) "obj::offsetExists"
string(14) "obj::offsetGet"
bool(false)

Wykonuje obj::offsetExists(), *nie* obj:offsetGet(), gdyż nie ma nic do pobrania
string(17) "obj::offsetExists"
bool(true)
