refentry.refsect1:
&reftitle.parameters;




   ftp

    &ftp.parameter.ftp;



   enable



     Jeśli argument ten ma wartośc &true;, tryb pasywny jest włączany. Jeśli
     nie, zostaje wyłączony.

  &reftitle.examples;



    Przykład użycia ftp_pasv


<?php
$file = 'somefile.txt';
$remote_file = 'readme.txt';

// nawiązanie połączenia
$ftp = ftp_connect($ftp_server);

// logowanie z użyciem nazwy użytkownika i hasła
$login_result = ftp_login($ftp, $ftp_user_name, $ftp_user_pass);

// włączenie trybu pasywnego
ftp_pasv($ftp, true);

// przesłanie pliku
if (ftp_put($ftp, $remote_file, $file, FTP_ASCII)) {
 echo "Plik $file został pomyślnie przesłany\n";
} else {
 echo "Napotkano problemy w czasie przesyłania pliku $file\n";
}

// zamknięcie połączenia
ftp_close($ftp);
?>
