refentry.refsect1:
&reftitle.description;

 booltidyNode::isComment




 Informuje, czy węzeł jest komentarzem.

  &reftitle.examples;



    Wyodrębnienie komentarzy ze złożonego dokumentu HTML


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<#
  /* kod JSTE */
  alert('Witaj świecie');
#>
</head>
<body>

<?php
  // kod PHP
  echo 'Witaj świecie!';
?>

<%
  /* kod ASP */
  response.write("Witaj świecie!")
%>

<!-- Comments -->
Witaj świecie
</body></html>
Poza HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html(), 'comment');

function get_nodes($node, $node_type) {

    // sprawdza, czy bieżący węzeł jest typu, o który pytamy
    if($node->{"is$node_type"}()) {
        echo "\n\n# $node_type node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // sprawdza, czy bieżący węzeł posiada potomków
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child, $node_type);
        }
    }
}

?>


    &example.outputs;


# węzeł JSTE #1
<#
  /* JSTE code */
  alert('Witaj świecie');
#>

